// HomePage.js
import React, { useState, useEffect } from 'react';
import CategoryListLeft from './CategoryListLeft';
import HomeTitle from './HomeTitle';
import {
    WeeklyPopularBooks,
    HumanitiesBooks,
    Arts_SportsBooks,
    ConvergenceBooks,
    SocietyBooks,
    NatureBooks,
    MedicineBooks,
    EducationBoosks,
    EngineeringBooks
} from './M_Books_Category';
import logo from '../Image/cats.png'

const left_category_width_size = 250



function HomePage() {
    const category_title = ["인문", "사회", "교육", "공학", "자연", "의약", "예체능", "융복합"];
    // 카테고리 선택을 위한 Dictionary
    const categoryComponentMap = {
        '주간 인기 책': WeeklyPopularBooks,
        '인문': HumanitiesBooks,
        '교육': EducationBooks, // 오타 수정(EducationBoosk -> EducationBooks)
        '사회': SocietyBooks,
        '공학': EngineeringBooks,
        '자연': NatureBooks,
        '의약': MedicineBooks,
        '예체능': Arts_SportsBooks,
        '융복합': ConvergenceBooks,
        // '고객센터': CustomerService, // 예시로 추가
    };


    // Add a state for the selected category
    const [selectedCategory, setSelectedCategory] = useState(null);

    // Handler for category selection
    const handleCategoryClick = (category) => {
        setSelectedCategory(category);
    };

    return (
        <div style={{ display: 'flex', minHeight: '100vh' }}>

            {/* 왼쪽 카테고리 리스트 관련 코드 */}
            <div style={{ position: 'relative', width: `${left_category_width_size}px`, borderRight: '1px solid gray' }}>

                <div style={{ display: 'block', flexDirection: 'column', alignItems: 'flex-start', width: `${left_category_width_size}px`, textAlign: 'left', overflow: 'hidden', paddingLeft: '45px', paddingBottom: '10px' }}>
                    <a href="#" >
                        <img src={logo} alt="logo" style={{ width: '100px', height: '100px', borderRadius: '50%', padding: '8%', marginTop: '30px' }} />
                    </a>
                    <HomeTitle key={`Eternal Library`} title={`Eternal Library`} />
                </div>

                <hr style={{ width: '100%', color: 'gray', borderTop: '1px solid gray' }} />
                <div style={{ display: 'block', flexDirection: 'column', alignItems: 'flex-start', width: `${left_category_width_size}px`, textAlign: 'left', overflow: 'hidden', paddingLeft: '20px' }}>
                    <CategoryListLeft categories={["주간 인기 책"]} onCategoryClick={handleCategoryClick} />
                </div>
                <hr style={{ width: '100%', color: 'gray', borderTop: '1px solid gray' }} />
                <br />
                <div style={{ display: 'block', flexDirection: 'column', alignItems: 'flex-start', width: `${left_category_width_size}px`, textAlign: 'left', overflow: 'hidden', paddingLeft: '20px' }}>
                    <CategoryListLeft categories={category_title} onCategoryClick={handleCategoryClick} />
                </div>
                <br />
                <hr style={{ width: '100%', color: 'gray', borderTop: '1px solid gray' }} />
                <br />
                <div style={{ display: 'block', flexDirection: 'column', alignItems: 'flex-start', width: `${left_category_width_size}px`, textAlign: 'left', overflow: 'hidden', paddingLeft: '20px' }}>
                    <CategoryListLeft categories={[`고객센터`]} onCategoryClick={handleCategoryClick} />
                </div>
            </div>
            {/* 왼쪽 카테고리 리스트 관련 코드 */}

            {/* 오른쪽 카테고리 리스트 관련 코드 */}
            <div style={{ flex: 1 }}>
                {
                    React.createElement(categoryComponentMap[selectedCategory], { text: 'test', image: logo })
                }
            </div>

            {/* 오른쪽 카테고리 리스트 관련 코드 */}
        </div>
    );
}

export default HomePage;
