// CategoryTitle.js
import React from 'react';
import styled from 'styled-components';

const Title = styled.a`
  display: block;
  margin-bottom: 1em;
  padding: 0.5em;
  text-decoration: none;
  color: black;
  border: 1px solid transparent;  // 항상 테두리가 있도록 설정
  ${props => props.border && 'border-bottom: 1px solid gray;'}  // 주간 인기 책에만 하단 border 설정

  &:focus {
    outline: none;
    border-color: #3477cf;  // 선택되었을 때 테두리 색상 변경
    background-color: #3477cf;  // 선택되었을 때 배경색 변경
    color: white;  // 선택되었을 때 글자색 변경
    width: 100%;  // 선택되었을 때의 너비를 부모 요소의 100%로 지정
  }
`;
function CategoryTitle({ title, border }) {
    return <Title href="#" border={border}>{title}</Title>;
}

export default CategoryTitle;
