// HomePage.js
import React from 'react';
import CategoryListLeft from './CategoryListLeft';
import CategoryTitle from './CategoryTitle';

const left_category_width_size = 250

function HomePage() {
    const category_title = ["인문", "사회", "교육", "공학", "자연", "의약", "예체능", "융복합"];
    return (
        <div style={{ position: 'relative', minHeight: '100vh', width: `${left_category_width_size}px`, borderRight: '1px solid gray' }}>
            <div></div>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', width: `${left_category_width_size}px` }}>
                <CategoryTitle key={`주간 인기 책`} title={`주간 인기 책`} />
            </div>
            <hr style={{ width: '100%', color: 'gray', borderTop: '1px solid gray' }} />
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', width: `${left_category_width_size}px` }}>
                <CategoryListLeft categories={category_title} />
            </div>
            <hr style={{ width: '100%', color: 'gray', borderTop: '1px solid gray' }} />
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', width: `${left_category_width_size}px` }}>
                <CategoryTitle key={`고객센터`} title={`고객센터`} />
            </div>
        </div>
    );
}

export default HomePage;
