import React, { forwardRef } from 'react';
import styled from 'styled-components';

const Title = styled.a`
  display: block;
  padding: 0.1em 100px 0.1em 20px;
  text-decoration: none;
  color: ${props => props.selected ? 'white' : 'black'};
  border: 1px solid ${props => props.selected ? '#3477cf' : 'transparent'};
  box-sizing: border-box;
  width: 230px;
  line-height: 2.5;
  background-color: ${props => props.selected ? '#3477cf' : 'transparent'};
  border-top-left-radius: ${props => props.selected ? '5px' : '0'};
  border-bottom-left-radius: ${props => props.selected ? '5px' : '0'};

  &:focus {
    outline: none;
  }
`;

const CategoryTitle = forwardRef((props, ref) => {
  return <Title ref={ref} selected={props.selected} href="#">{props.title}</Title>;
});

export default CategoryTitle;
