// HomePage.js
import React, { useState, useRef } from 'react';
import CategoryListLeft from './CategoryListLeft';
import CategoryTitle from './CategoryTitle';
import HomeTitle from './HomeTitle';
import logo from '../Image/cats.png'

const left_category_width_size = 250



function HomePage() {
    const category_title = ["인문", "사회", "교육", "공학", "자연", "의약", "예체능", "융복합"];

    // 선택된 카테고리를 관리하는 상태 추가
    const [selectedCategory, setSelectedCategory] = useState(true);

    // "주간 인기 책"이 선택되었는지 여부를 나타내는 상태
    const [popularSelected, setPopularSelected] = useState(true);

    // "주간 인기 책"을 렌더링하는 컴포넌트에 대한 참조
    const popularRef = useRef(null);

    // 컴포넌트가 마운트되었을 때 "주간 인기 책"에 포커스를 줍니다.
    useEffect(() => {
        if (popularRef.current) {
            popularRef.current.focus();
        }
    }, []);

    // "Eternal Library"를 클릭했을 때 실행되는 함수
    const handleLogoClick = () => {
        setSelectedCategory(true);
        // "주간 인기 책"을 선택하도록 상태를 업데이트합니다.
        setPopularSelected(true);
        // "주간 인기 책"에 포커스를 줍니다.
        if (popularRef.current) {
            popularRef.current.focus();
        }
    };

    return (
        <div style={{ position: 'relative', minHeight: '100vh', width: `${left_category_width_size}px`, borderRight: '1px solid gray' }}>
            <div style={{ display: 'block', flexDirection: 'column', alignItems: 'flex-start', width: `${left_category_width_size}px`, textAlign: 'left', overflow: 'hidden', paddingLeft: '45px', paddingBottom: '10px' }}>
                <a href="#" onClick={handleLogoClick}>
                    <img src={logo} alt="logo" style={{ width: '100px', height: '100px', borderRadius: '50%', padding: '8%', marginTop: '30px' }} />
                </a>
                <HomeTitle key={`Eternal Library`} title={`Eternal Library`} />
            </div>

            <hr style={{ width: '100%', color: 'gray', borderTop: '1px solid gray' }} />
            <div style={{ display: 'block', flexDirection: 'column', alignItems: 'flex-start', width: `${left_category_width_size}px`, textAlign: 'left', overflow: 'hidden', paddingLeft: '20px' }}>
                <CategoryTitle key={`주간 인기 책`} title={`주간 인기 책`} selected={popularSelected} ref={popularRef} />
            </div>
            <hr style={{ width: '100%', color: 'gray', borderTop: '1px solid gray' }} />
            <br />
            <div style={{ display: 'block', flexDirection: 'column', alignItems: 'flex-start', width: `${left_category_width_size}px`, textAlign: 'left', overflow: 'hidden', paddingLeft: '20px' }}>
                <CategoryListLeft categories={category_title} />
            </div>
            <br />
            <hr style={{ width: '100%', color: 'gray', borderTop: '1px solid gray' }} />
            <br />
            <div style={{ display: 'block', flexDirection: 'column', alignItems: 'flex-start', width: `${left_category_width_size}px`, textAlign: 'left', overflow: 'hidden', paddingLeft: '20px' }}>
                <CategoryTitle key={`고객센터`} title={`고객센터`} />
            </div>
        </div>
    );
}

export default HomePage;
