// CategoryTitle.js
import React from 'react';
import styled from 'styled-components';

const Title = styled.a`
  display: block;
  margin-bottom: 0.7em;
  padding: 0.5em;
  text-decoration: none;
  color: black;
  border: 1px solid transparent;  // 기본 상태에서도 테두리를 가지도록 변경
  box-sizing: border-box;  // 상자의 크기 계산 방법을 변경

  &:focus {
    outline: none;
    border-color: #3477cf;  // 선택되었을 때 테두리 색상 변경
    background-color: #3477cf;  // 선택되었을 때 배경색 변경
    color: white;  // 선택되었을 때 글자색 변경
    margin-left: 1em;  // 왼쪽 여백 추가
    width: 10px;  // padding을 포함한 너비를 100%로 만들어 줌
  }
`;

function CategoryTitle(props) {
    return <Title href="#">{props.title}</Title>;
}

export default CategoryTitle;
