// CategoryTitle.js
import React from 'react';
import styled from 'styled-components';

const Title = styled.a`
  display: block;
  padding: 0.1em 100px 0.1em 20px; // 상하 패딩 0.1em, 좌우 패딩 20px/100px로 설정
  text-decoration: none;
  color: black;
  border: 1px solid transparent;  // 기본 상태에서도 테두리를 가지도록 변경
  box-sizing: border-box;  // 상자의 크기 계산 방법을 변경
  width: 230px; // 파란색 상자 너비변경 - homepage에서 전체 패딩 수정 시 이쪽 부분도 같이 수정해야함
  line-height: 2.5; // 글자 간의 간격 늘림

  // selected prop에 따라 스타일을 변경
  ${props => props.selected && `
    border-color: #3477cf;
    background-color: #3477cf;
    color: white;
    padding: 0.1em 100px 0.1em 20px;
    line-height: 2.5;
    border-top-left-radius: 3px;
    border-bottom-left-radius: 3px;
    font-weight : 700;
  `}
`;

function CategoryTitle({ title, selected }) {
  return <Title selected={selected} href="#">{title}</Title>;
}

export default CategoryTitle;
