// CategoryTitle.js
import React from 'react';
import styled from 'styled-components';

const Title = styled.a`
  display: block;
  margin-bottom: 1em;
  text-decoration: none;
  color: black;
  white-space: nowrap; // 줄바꿈 방지

  &:focus {
    outline: none;
    border: 1px solid #3477cf; // 테두리 색상 변경
    background-color: #3477cf; // 배경색 변경
    color: white; // 텍스트 색상을 배경색과 대비되게 변경
    width: 100%; // 너비를 최대로 늘림
  }
`;

function CategoryTitle(props) {
    return <Title href="#">{props.title}</Title>;
}

export default CategoryTitle;
