import React from 'react';
import styled, { createGlobalStyle } from 'styled-components';


// Google Fonts에서 'Noto Sans KR'를 불러오는 전역 스타일을 생성합니다
const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@400;700&display=swap');
  body {
    font-family: 'Apple SD Gothic Neo', 'Noto Sans KR', sans-serif;
  }
`;

const Title = styled.a`
  display: block;
  padding: 0.1em 100px 0.1em 20px; // 상하 패딩 0.1em, 좌우 패딩 20px/100px로 설정
  text-decoration: none;
  color: black;
  border: 1px solid transparent;  // 기본 상태에서도 테두리를 가지도록 변경
  box-sizing: border-box;  // 상자의 크기 계산 방법을 변경
  width: 230px; // 파란색 상자 너비변경 - homepage에서 전체 패딩 수정 시 이쪽 부분도 같이 수정해야함
  line-height: 2.5; // 글자 간의 간격 늘림

  font-family: 'Apple SD Gothic Neo', 'Noto Sans KR', sans-serif; // 폰트 설정
  `;

function HomeTitle(props) {
  return <Title href="#">{props.title}</Title>;
}

export default HomeTitle;
